void dump_vtable(const char* name, DWORD* vtable)
{
    printf("%s|%08X|%08X\n", name, vtable[0], vtable[1]);
}

void hook_slf_vtable(void* decons, void* action, DWORD* vtable)
{
    vtable[0] = (DWORD)decons;
    vtable[1] = (DWORD)action;
}

